'use client';

import ChoicesFormInput from '@/components/from/ChoicesFormInput';
import Nouislider from 'nouislider-react';
import { useState } from 'react';
import { Button, Card, CardBody, CardFooter, CardHeader, CardTitle, Col, Row } from 'react-bootstrap';
import { propertyData } from './propertyData'; // Importe seus dados

const PropertiesFilter = () => {
    const [selectedValue, setSelectedValue] = useState([0, 120000]); // Valores padrão ajustados
    const [selectedLocation, setSelectedLocation] = useState('');
    const [selectedType, setSelectedType] = useState('');
    const [selectedBedrooms, setSelectedBedrooms] = useState([]);
    const [selectedFeatures, setSelectedFeatures] = useState([]);


    const handleSliderChange = values => {
        setSelectedValue(values);
    };

    const handleInputChange = event => {
        const newValue = Math.round(event.target.value);
        if (event.target.id === 'minCost' && newValue <= selectedValue[1]) {
            setSelectedValue([newValue, selectedValue[1]]);
        } else if (event.target.id === 'maxCost' && newValue >= selectedValue[0]) {
            setSelectedValue([selectedValue[0], newValue]);
        }
    };

    const handleLocationChange = (value) => {
        setSelectedLocation(value);
    };

    const handleTypeChange = (event) => {
        setSelectedType(event.target.value);
    };

    const handleBedroomChange = (event) => {
        const bedroom = event.target.id;
        if (event.target.checked) {
            setSelectedBedrooms([...selectedBedrooms, bedroom]);
        } else {
            setSelectedBedrooms(selectedBedrooms.filter(item => item !== bedroom));
        }
    };

    const handleFeatureChange = (event) => {
        const feature = event.target.id.replace('defaultCheck', ''); // Remove "defaultCheck" do ID
        if (event.target.checked) {
            setSelectedFeatures([...selectedFeatures, feature]);
        } else {
            setSelectedFeatures(selectedFeatures.filter(item => item !== feature));
        }
    };

    const filterProperties = () => {
        const filteredProperties = propertyData.filter(property => {
            const priceInRange = property.size >= selectedValue[0] && property.size <= selectedValue[1];
            const locationMatches = selectedLocation === '' || property.location === selectedLocation;
            const typeMatches = selectedType === '' || property.propertyType === selectedType;
            const bedroomMatches = selectedBedrooms.length === 0 || selectedBedrooms.includes(property.beds + " BHK"); // Assumindo que 'beds' corresponda a 'BHK'
            const featureMatches = selectedFeatures.length === 0 || selectedFeatures.every(feature => {
                // Adapte conforme a estrutura dos seus dados. Exemplo:
                return (property.variant && property.variant.includes(feature)) || (property.type && property.type.includes(feature));

            });

            return priceInRange && locationMatches && typeMatches && bedroomMatches && featureMatches;
        });
        // Faça algo com filteredProperties, como atualizar o estado de um componente pai ou usar um contexto para disponibilizar os dados filtrados.
        console.log(filteredProperties); // Apenas um exemplo: exibe os resultados no console
    };


    const uniqueLocations = [...new Set(propertyData.map(item => item.location))];
    const uniqueTypes = [...new Set(propertyData.map(item => item.propertyType))];

    return (
        <Col xl={3} lg={12}>
            <Card>
                {/* ... (resto do seu código) */}

                <label htmlFor="choices-single-groups" className="form-label">
                    Properties Location
                </label>
                <ChoicesFormInput
                    className="form-control"
                    id="choices-single-groups"
                    data-placeholder="Select City"
                    onChange={handleLocationChange} // Adicione o onChange
                    value={selectedLocation}
                >
                    <option value="">Choose a city</option>
                    {uniqueLocations.map(location => (
                        <option key={location} value={location}>{location}</option>
                    ))}
                </ChoicesFormInput>

                <div className="mb-3">
                    <label htmlFor="typeplace" className="form-label">
                        Type Of Place
                    </label>
                    <ChoicesFormInput
                        className="form-control"
                        id="typeplace"
                        onChange={handleTypeChange} // Adicione o onChange
                        value={selectedType}
                    >
                        <option value="">Choose a type</option>
                        {uniqueTypes.map(type => (
                            <option key={type} value={type}>{type}</option>
                        ))}
                    </ChoicesFormInput>
                </div>

                {/* ... (resto do seu código) */}

                <h5 className="text-dark fw-medium my-3">Bedrooms :</h5>
                <div role="group" aria-label="Basic checkbox toggle button group">
                    {[1, 2, 3, "4 & 5"].map(num => (
                        <React.Fragment key={num}>
                            <input
                                type="checkbox"
                                className="btn-check"
                                id={`btncheck${num}`}
                                onChange={handleBedroomChange}
                                checked={selectedBedrooms.includes(`${num} BHK`)}
                            />
                            <label className="btn btn-outline-primary me-1" htmlFor={`btncheck${num}`}>
                                {num} BHK
                            </label>
                        </React.Fragment>
                    ))}
                </div>

                {/* ... Outras opções de filtro */}

                <CardFooter>
                    <Button variant="primary" className="w-100" onClick={filterProperties}>
                        Apply
                    </Button>
                </CardFooter>
            </Card>
        </Col>
    );
};

export default PropertiesFilter;
