'use client';

import { signIn } from 'next-auth/react';
import { useRouter } from 'next/navigation';
import { useState } from 'react';
import { useForm } from 'react-hook-form';
import * as yup from 'yup';
import { yupResolver } from '@hookform/resolvers/yup';
import { useNotificationContext } from '@/context/useNotificationContext';
import useQueryParams from '@/hooks/useQueryParams';

const useSignIn = () => {
    const [loading, setLoading] = useState(false);
    const { push } = useRouter();
    const { showNotification } = useNotificationContext();
    const queryParams = useQueryParams(); // Se voc칡 precisa de query params para outros fins
    // ... (seu schema yup)

    const login = handleSubmit(async (values) => {
        setLoading(true);

        const callbackUrl = queryParams['redirectTo'] ?? '/'; // P치gina padr칚o se n칚o houver redirectTo

        signIn('credentials', {
            redirect: false, // N칚o redirecione automaticamente
            email: values?.email,
            password: values?.password,
            callbackUrl: callbackUrl // Passe o callbackUrl para o NextAuth
        }).then(res => {
            if (res?.ok) {
                // N칚o precisa redirecionar aqui. O NextAuth cuidar치 disso.
                showNotification({
                    message: 'Successfully logged in. Redirecting....',
                    variant: 'success'
                });
            } else {
                showNotification({
                    message: res?.error ?? '',
                    variant: 'danger'
                });
            }
        }).finally(() => setLoading(false));
    });

    return { loading, login, control };
};

export default useSignIn;
