'use client';

import Image from 'next/image';
import { Card, CardBody, Col, Row } from 'react-bootstrap';

// Importe as imagens (substitua pelos caminhos corretos)
import logoEmpresa from '@/assets/images/logo_empresa.png'; // Logo da empresa
import qrCodeVoucher from '@/assets/images/qrcode.png'; // QR Code do voucher
import voucherImage from '@/assets/images/no-qrcode.png'; // Imagem padrão do voucher

const VoucherCard = ({ voucher }) => { // voucher é um objeto com as informações do voucher

    return (
        <Col xl={4}> {/* Largura do card */}
            <Card className="bg-dark bg-gradient">
                <CardBody>
                    <Row className="align-items-center"> {/* Alinhamento vertical */}
                        <Col xl={7} lg={6} md={6}>
                            <Row>
                                <Col xs={12}>
                                    <Image
                                        src={logoEmpresa} // Logo da empresa
                                        alt="Logo da Empresa"
                                        width={100}
                                        height={100}
                                        className="mb-2"
                                    />
                                </Col>
                            </Row>
                            <h3 className="text-white fw-bold">{voucher.nome}</h3> {/* Nome do voucher */}
                            <p className="text-white h5">{voucher.descricao}</p> {/* Descrição */}
                            <p className="text-white-50">Código: {voucher.codigo}</p> {/* Código */}
                            <p className="text-white-50">Validade: {voucher.validade}</p> {/* Validade */}

                        </Col>
                        <Col xl={5} lg={4} md={4} className="text-center">
                            <Image src={qrCodeVoucher} alt="QR Code" width={150} height={150} /> {/* QR Code */}
                        </Col>
                    </Row>
                </CardBody>
            </Card>
        </Col>
    );
};

export default VoucherCard;
