import { Pool } from "pg";
import { getServerSession } from "next-auth";
import { options } from "@/app/api/auth/[...nextauth]/options"; // Configura√ß√£o do NextAuth

const pool = new Pool({
  connectionString: process.env.DATABASE_URL,
});

export async function GET(req) {
  try {
    const session = await getServerSession(options);

    if (!session || session.user.role !== "admin") {
      return new Response(JSON.stringify({ error: "Acesso negado" }), { status: 403 });
    }

    console.log("üì° Buscando lista de parceiros...");

    const result = await pool.query("SELECT id, nome_empresa, email, foto FROM parceiros");

    return new Response(JSON.stringify(result.rows), {
      status: 200,
      headers: { "Content-Type": "application/json" },
    });

  } catch (error) {
    console.error("‚ùå Erro ao buscar parceiros:", error);
    return new Response(JSON.stringify({ error: "Erro ao buscar parceiros" }), { status: 500 });
  }
}

export async function POST(req) {
  try {
    const session = await getServerSession(options);

    if (!session || session.user.role !== "admin") {
      return new Response(JSON.stringify({ error: "Acesso negado" }), { status: 403 });
    }

    const { nome_empresa, email, senha, foto } = await req.json();

    if (!nome_empresa || !email || !senha) {
      return new Response(JSON.stringify({ error: "Nome, email e senha s√£o obrigat√≥rios" }), { status: 400 });
    }

    console.log("üì° Criando novo parceiro:", email);

    const result = await pool.query(
      "INSERT INTO parceiros (nome_empresa, email, senha, foto, data_criacao) VALUES ($1, $2, $3, $4, NOW()) RETURNING id, nome_empresa, email, foto",
      [nome_empresa, email, senha, foto || "/assets/images/users/default-avatar.jpg"]
    );

    return new Response(JSON.stringify({ success: true, parceiro: result.rows[0] }), {
      status: 201,
      headers: { "Content-Type": "application/json" },
    });

  } catch (error) {
    console.error("‚ùå Erro ao criar parceiro:", error);
    return new Response(JSON.stringify({ error: "Erro ao criar parceiro" }), { status: 500 });
  }
}

export async function PUT(req) {
  try {
    const session = await getServerSession(options);

    if (!session || session.user.role !== "admin") {
      return new Response(JSON.stringify({ error: "Acesso negado" }), { status: 403 });
    }

    const { id, nome_empresa, email, foto } = await req.json();

    if (!id || !nome_empresa || !email) {
      return new Response(JSON.stringify({ error: "ID, Nome e Email s√£o obrigat√≥rios" }), { status: 400 });
    }

    console.log("üì° Atualizando parceiro:", id);

    const result = await pool.query(
      "UPDATE parceiros SET nome_empresa = $1, email = $2, foto = $3 WHERE id = $4 RETURNING id, nome_empresa, email, foto",
      [nome_empresa, email, foto, id]
    );

    if (result.rows.length === 0) {
      return new Response(JSON.stringify({ error: "Parceiro n√£o encontrado" }), { status: 404 });
    }

    return new Response(JSON.stringify({ success: true, parceiro: result.rows[0] }), {
      status: 200,
      headers: { "Content-Type": "application/json" },
    });

  } catch (error) {
    console.error("‚ùå Erro ao atualizar parceiro:", error);
    return new Response(JSON.stringify({ error: "Erro ao atualizar parceiro" }), { status: 500 });
  }
}

export async function DELETE(req) {
  try {
    const session = await getServerSession(options);

    if (!session || session.user.role !== "admin") {
      return new Response(JSON.stringify({ error: "Acesso negado" }), { status: 403 });
    }

    const { ids } = await req.json();

    if (!ids || !Array.isArray(ids) || ids.length === 0) {
      return new Response(JSON.stringify({ error: "IDs inv√°lidos" }), { status: 400 });
    }

    console.log("üóëÔ∏è Excluindo parceiros:", ids);

    const result = await pool.query("DELETE FROM parceiros WHERE id = ANY($1) RETURNING id", [ids]);

    if (result.rowCount === 0) {
      return new Response(JSON.stringify({ error: "Nenhum parceiro encontrado para exclus√£o" }), { status: 404 });
    }

    return new Response(JSON.stringify({ success: true, deleted: result.rows }), {
      status: 200,
      headers: { "Content-Type": "application/json" },
    });

  } catch (error) {
    console.error("‚ùå Erro ao excluir parceiros:", error);
    return new Response(JSON.stringify({ error: "Erro ao excluir parceiros" }), { status: 500 });
  }
}
